/* http://meyerweb.com/eric/tools/css/reset/ 
   v2.0 | 20110126
   License: none (public domain)
*/
@import url("https://fonts.googleapis.com/css?family=Kanit:200,200i,400,400i,600,600i,700,700i,800,800i");
html, body, div, span, applet, object, iframe,
h1, h2, h3, h4, h5, h6, p, blockquote, pre,
a, abbr, acronym, address, big, cite, code,
del, dfn, em, img, ins, kbd, q, s, samp,
small, strike, strong, sub, sup, tt, var,
b, u, i, center,
dl, dt, dd, ol, ul, li,
fieldset, form, label, legend,
table, caption, tbody, tfoot, thead, tr, th, td,
article, aside, canvas, details, embed,
figure, figcaption, footer, header, hgroup,
menu, nav, output, ruby, section, summary,
time, mark, audio, video {
  margin: 0;
  padding: 0;
  border: 0;
  font-size: 100%;
  font: inherit;
  vertical-align: baseline;
}

/* HTML5 display-role reset for older browsers */
article, aside, details, figcaption, figure,
footer, header, hgroup, menu, nav, section {
  display: block;
}

body {
  line-height: 1;
}

ol, ul {
  list-style: none;
}

blockquote, q {
  quotes: none;
}

blockquote:before, blockquote:after,
q:before, q:after {
  content: "";
  content: none;
}

table {
  border-collapse: collapse;
  border-spacing: 0;
}

.section {
  min-height: 100vh;
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;
  overflow: hidden;
}
.section:first-child {
  background: linear-gradient(red, yellow);
}
.section:nth-child(3) {
  background: linear-gradient(yellow, red);
}

.rellax {
  position: absolute;
  opacity: 1;
  top: 0;
}
.rellax:nth-child(1) {
  left: 5%;
  top: 40%;
}
.rellax:nth-child(2) {
  left: 8.8%;
  top: 40%;
}
.rellax:nth-child(3) {
  left: 13%;
  top: 40%;
}
.rellax:nth-child(4) {
  left: 17%;
  top: 40%;
}
.rellax:nth-child(5) {
  left: 20%;
  top: 40%;
}

.github {
  left: 90%;
  top: 2%;
  width: 60px;
  height: 60px;
  position: absolute;
  -webkit-transition: all 0.2s ease;
}
.github:hover {
  transform: scale(1.2);
}

.linkedin {
  left: 94%;
  top: 2%;
  width: 60px;
  height: 60px;
  position: absolute;
  -webkit-transition: all 0.2s ease;
}
.linkedin:hover {
  transform: scale(1.2);
}

.section--horizontal {
  height: 300vw;
}
.section--horizontal .section__inner {
  width: 300vw;
  height: 100vh;
  background: linear-gradient(to right, red, yellow);
  position: absolute;
  top: 0;
  left: 0;
}

.section--isFixed .section__inner {
  position: fixed;
}

.section--isScrolled .section__inner {
  bottom: 0;
  top: auto;
}

.products {
  display: flex;
}

.products__product {
  display: flex;
  margin-left: 1.5%;
  margin-right: 2%;
  position: relative;
  top: 225px;
  width: 100vw;
}
.products__product p {
  font-size: 20px;
  padding-left: 35px;
  font-style: normal;
}
.products__product p::first-letter {
  color: red;
  font-size: 60px;
  line-height: 45px;
  margin-right: 5px;
}
.products__product img {
  width: 700px;
  height: 450px;
  filter: grayscale(100%);
}
.products__product img:hover {
  filter: none;
  transition: all 0.3s ease;
}

.section--start p {
  font-family: "Kanit", sans-serif;
  font-size: 80px;
  font-style: italic;
  text-transform: uppercase;
  color: transparent;
  -webkit-background-clip: text;
  background-image: url("58e80f86e753fc86ec026428ac8be3ea.jpg");
  transform: translate(-50%, -50%);
}
.section--start p:hover {
  background-size: 80% 80%;
}
